import React, { useRef } from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import { useScroll, motion, useTransform, useSpring } from "framer-motion";
import { useViewportScroll } from "framer-motion";

const Model: React.FC = () => {
  const gltf = useGLTF("/models/model.glb", true);
  return <primitive object={gltf.scene} scale={2.5} />;
};

const Hero: React.FC = () => {
  const textRef = useRef<HTMLHeadingElement>(null);
  const { scrollY } = useViewportScroll();

  // 让文字在 scroll 过程中横向移动
  const x = useTransform(scrollY, [0, 400], [0, -150]);
  const xSpring = useSpring(x, { stiffness: 100, damping: 20 });

  return (
    <section className="relative w-full h-[100vh] overflow-hidden bg-white">
      {/* Three.js 背景 */}
      <Canvas className="absolute top-0 left-0 w-full h-full z-0">
        <ambientLight intensity={0.4} />
        <directionalLight position={[5, 5, 5]} />
        <Model />
        <OrbitControls enableZoom={false} autoRotate />
      </Canvas>

      {/* 居中文字（随着 scroll 向左移动） */}
      <motion.h1
        ref={textRef}
        className="relative z-10 text-[13vw] font-serif font-bold text-center mt-[35vh]"
        style={{ x: xSpring }}
      >
        CELESTE
      </motion.h1>
    </section>
  );
};

export default Hero;
