import React, { useRef, useState, useEffect } from "react";
import { motion, useScroll, useTransform, useSpring, AnimatePresence, useMotionValue, useDragControls } from "framer-motion";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines";
import Footer from "./Footer";

const Pawmeet: React.FC = () => {
  const { scrollY } = useScroll();
  const scrollX = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(scrollX, { stiffness: 100, damping: 30 });
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const [currentDesignIndex, setCurrentDesignIndex] = useState(0);
  const topImages = ["/p-1.png", "/p-2.png", "/p-3.png"];
  const designImages = ["/p_1.png", "/p_2.png", "/p_3.png", "/p_4.png"];
  const dragControls = useDragControls();
  const dragX = useMotionValue(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % topImages.length);
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  const handleDrag = (event: any, info: any) => {
    const dragDistance = info.offset.x;
    const sensitivity = 0.5;
    const newIndex = (Math.floor(dragDistance * sensitivity) + currentDesignIndex + designImages.length) % designImages.length;
    setCurrentDesignIndex(newIndex);
  };

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <section className="relative w-full min-h-[100vh] h-screen overflow-hidden">
        <InteractiveLines />
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-20 translate-y-50"
            animate={{
              y: [0, -20, 0],
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <motion.img 
              src="/card1.png" 
              alt="Card" 
              className="w-80 h-auto"
              whileHover={{
                scale: 1.1,
                transition: {
                  duration: 0.3,
                  ease: "easeOut"
                }
              }}
            />
          </motion.div>
        </div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          PAWMEET
        </motion.h1>
      </section>
      
      <section className="relative w-full h-[900px] bg-gradient-to-b from-[#B2FFC8] via-[#F5F2ED] via-[#F5F2ED] via-[#F5F2ED] to-[#F5F2ED] py-10 px-8 flex items-center justify-center overflow-hidden">
        {/* Background paw prints */}
        <div className="absolute w-[280px] h-[280px] left-20 bottom-20 rotate-[-15deg]">
          <img 
            src="/p-bg.png" 
            alt="Background Paw Print" 
            className="w-full h-full object-contain"
          />
        </div>
        <div className="absolute w-[280px] h-[280px] right-20 top-20 rotate-[15deg]">
          <img 
            src="/p-bg.png" 
            alt="Background Paw Print" 
            className="w-full h-full object-contain"
          />
        </div>

        <div className="absolute top-10 w-full overflow-hidden">
          <motion.div 
            className="whitespace-nowrap text-4xl font-light italic font-inter text-black"
            initial={{ x: "100%" }}
            animate={{ x: "-100%" }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "linear"
            }}
          >
            Front-end and Back-end Development
          </motion.div>
        </div>
        
        <div className="relative flex items-center justify-center">
          <motion.div
            initial={{ y: -100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{
              duration: 1,
              ease: "easeOut"
            }}
            className="absolute -top-40 w-[600px] h-[600px] z-20"
          >
            <AnimatePresence mode="wait">
              <motion.img 
                key={currentImageIndex}
                src={topImages[currentImageIndex]}
                alt={`Project Image ${currentImageIndex + 1}`}
                className="w-full h-full object-contain"
                loading="eager"
                decoding="sync"
                fetchPriority="high"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{
                  duration: 0.5,
                  ease: "easeInOut"
                }}
              />
            </AnimatePresence>
          </motion.div>
          <h1 className="text-[120px] font-semibold italic font-inter">UI/UX DESIGN</h1>
        </div>
      </section>

      <section className="relative w-full h-[500px]">
        <img 
          src="/p-bg2.png" 
          alt="Background" 
          className="w-full h-full object-cover object-center"
        />
      </section>

      <section className="relative w-full min-h-screen bg-[#F5F5F0] py-20 px-8">
        <div className="max-w-7xl mx-auto flex items-center gap-8">
          <div className="flex-1 pl-32 -mt-20">
            <div className="w-80 h-[2px] bg-[#8BE644] mb-8"></div>
            <div className="flex items-center gap-4 mb-8">
              <img 
                src="/paw.png" 
                alt="Paw Icon" 
                className="w-12 h-12"
              />
              <h2 className="text-6xl font-bold">About</h2>
            </div>
            <p className="text-xl leading-relaxed italic text-gray-700">
              "PAWMEET" is a pet social networking app designed to help pet owners find 
              compatible playmates for their pets and connect within a pet-friendly community. 
              Users can help their pets match playmates, arrange playdates, access 
              pet-related services.
            </p>
          </div>
          
          <div className="flex-1 flex justify-center">
            <div className="relative w-[250px]">
              <img 
                src="/p-mobile.png" 
                alt="Phone Mockup" 
                className="w-full h-auto"
              />
            </div>
          </div>
        </div>
      </section>

      <section className="relative w-full min-h-screen bg-[#F5F5F0] px-8 -mt-40">
        <div className="max-w-7xl mx-auto">
          {/* Typography Section */}
          <div className="mb-20 -ml-24">
            <div className="w-80 h-[2px] bg-[#8BE644] mb-4 ml-24 mt-8"></div>
            <div className="grid gap-16">
              <div className="font-epilogue">
                <h3 className="text-[8vw] tracking-wide italic mb-4">AaBbCcDdEeFfGgHhIiJjKkLl</h3>
                <p className="text-xl text-gray-600 italic ml-24">Epilogue</p>
              </div>
              <div>
                <h3 className="font-inter text-[8vw] tracking-wide mb-4">AaBbCcDdEeFfGgHhIiJjKkLl</h3>
                <p className="text-xl text-gray-600 italic ml-24">Inter</p>
              </div>
            </div>
          </div>

          {/* Color Palette */}
          <div className="mb-20">
            <div className="flex w-full gap-8">
              <div className="w-[40%] grid grid-cols-2 gap-10">
                <div className="w-full h-40 bg-[#1E1E1E] relative rounded-[16px]">
                  <span className="absolute bottom-1 left-1 text-white text-xs">#1E1E1E</span>
                </div>
                <div className="w-full h-40 bg-[#BCEF94] border border-black relative rounded-[16px]">
                  <span className="absolute bottom-1 left-1 text-black text-xs">#BCEF94</span>
                </div>
                <div className="w-full h-40 bg-[#F2F2F2] border border-black relative rounded-[16px]">
                  <span className="absolute bottom-1 left-1 text-black text-xs">#F2F2F2</span>
                </div>
                <div className="w-full h-40 bg-[#61BC19] relative rounded-[16px]">
                  <span className="absolute bottom-1 left-1 text-white text-xs">#61BC19</span>
                </div>
              </div>
              <div className="w-[60%] h-[360px] bg-[#8BE644] flex items-center justify-center relative rounded-[16px]">
                <span className="absolute bottom-1 left-1 text-black text-xs">#8BE644</span>
              </div>
            </div>
          </div>
        </div>

        {/* Design Showcase */}
        <div className="w-screen -mx-8">
          <div className="bg-black p-24 rounded-lg shadow-lg w-full min-h-[600px] flex items-center justify-center">
            <motion.div
              className="relative w-full h-full flex items-center justify-center"
              drag="x"
              dragControls={dragControls}
              dragConstraints={{ left: -1800, right: 1800 }}
              onDrag={(event, info) => {
                const dragDistance = info.offset.x;
                const sensitivity = 1.2;
                const newIndex = (Math.floor(dragDistance * sensitivity) + currentDesignIndex + designImages.length) % designImages.length;
                setCurrentDesignIndex(newIndex);
              }}
            >
              <div className="flex gap-8 items-center">
                {designImages.map((image, index) => {
                  const isActive = index === currentDesignIndex;
                  return (
                    <motion.img
                      key={index}
                      src={image}
                      alt={`Design ${index + 1}`}
                      className="h-[500px] w-auto object-contain cursor-pointer"
                      style={{
                        opacity: isActive ? 1 : 0.4,
                        scale: isActive ? 1 : 0.95
                      }}
                      transition={{ duration: 0.15 }}
                    />
                  );
                })}
              </div>
            </motion.div>
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Pawmeet;
