import React, { useState } from 'react';
import { motion } from 'framer-motion';

const InteractiveLines: React.FC = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Generate positions for elements
  const generateElements = () => {
    return {
      circles: [
        { x: 25, y: 25, size: 80 }, // Much larger circles
        { x: 75, y: 25, size: 80 },
        { x: 25, y: 75, size: 80 },
        { x: 75, y: 75, size: 80 },
      ],
      lines: [
        { x: 25, y: 25, angle: 45, length: 150 }, // Much longer lines
        { x: 75, y: 25, angle: 135, length: 150 },
        { x: 25, y: 75, angle: -45, length: 150 },
        { x: 75, y: 75, angle: -135, length: 150 },
      ]
    };
  };

  const { circles, lines } = generateElements();

  const handleMouseMove = (e: React.MouseEvent) => {
    const { clientX, clientY } = e;
    setMousePosition({
      x: (clientX / window.innerWidth) * 100,
      y: (clientY / window.innerHeight) * 100
    });
  };

  return (
    <div 
      className="absolute inset-0 overflow-hidden"
      onMouseMove={handleMouseMove}
    >
      <motion.div
        className="absolute top-0 left-0 w-full h-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {/* Circles */}
        {circles.map((circle, index) => {
          const distance = Math.sqrt(
            Math.pow(circle.x - mousePosition.x, 2) + 
            Math.pow(circle.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.5, 1.5 - distance / 30);

          return (
            <motion.div
              key={`circle-${index}`}
              className="absolute rounded-full"
              style={{
                left: `${circle.x}%`,
                top: `${circle.y}%`,
                width: `${circle.size}px`,
                height: `${circle.size}px`,
                background: 'radial-gradient(circle, #4CAF50, #8BC34A)',
                transform: 'translate(-50%, -50%)',
                boxShadow: '0 0 30px rgba(76, 175, 80, 0.8)',
              }}
              initial={{ scale: 0 }}
              animate={{ 
                scale: scale,
                rotate: Math.sin(Date.now() * 0.001 + index) * 360,
                y: Math.sin(Date.now() * 0.002 + index) * 20,
              }}
              transition={{
                duration: 0.3,
                ease: "easeInOut"
              }}
            />
          );
        })}

        {/* Lines */}
        {lines.map((line, index) => {
          const distance = Math.sqrt(
            Math.pow(line.x - mousePosition.x, 2) + 
            Math.pow(line.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.5, 1.5 - distance / 30);

          return (
            <motion.div
              key={`line-${index}`}
              className="absolute origin-left"
              style={{
                left: `${line.x}%`,
                top: `${line.y}%`,
                width: `${line.length}px`,
                height: '4px',
                background: 'linear-gradient(to right, #4CAF50, #8BC34A)',
                transform: `rotate(${line.angle}deg)`,
                transformOrigin: '0 50%',
                boxShadow: '0 0 20px rgba(76, 175, 80, 0.8)',
              }}
              initial={{ scaleX: 0 }}
              animate={{ 
                scaleX: scale,
                rotate: line.angle + Math.sin(Date.now() * 0.001 + index) * 10,
                y: Math.sin(Date.now() * 0.002 + index) * 10,
              }}
              transition={{
                duration: 0.3,
                ease: "easeInOut"
              }}
            />
          );
        })}

        {/* Mouse Trail */}
        <motion.div
          className="absolute rounded-full pointer-events-none"
          style={{
            width: '150px',
            height: '150px',
            background: 'radial-gradient(circle, rgba(76, 175, 80, 0.3), transparent 70%)',
            boxShadow: '0 0 40px rgba(76, 175, 80, 0.3)',
          }}
          animate={{
            x: mousePosition.x * window.innerWidth / 100 - 75,
            y: mousePosition.y * window.innerHeight / 100 - 75,
            scale: [1, 1.2, 1],
          }}
          transition={{ 
            type: "spring", 
            stiffness: 50, 
            damping: 20,
            scale: {
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }
          }}
        />
      </motion.div>
    </div>
  );
};

export default InteractiveLines;
