import React, { useEffect, useRef } from 'react';

const InteractiveLines: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    // 线条数组
    const lines: { x1: number; y1: number; x2: number; y2: number; opacity: number }[] = [];
    const maxLines = 30;

    // 粒子数组
    const particles: { x: number; y: number; size: number; opacity: number; speedX: number; speedY: number }[] = [];
    const maxParticles = 100;

    const createLine = (x: number, y: number) => {
      const angle = Math.random() * Math.PI * 2;
      const length = Math.random() * 200 + 100;
      const x2 = x + Math.cos(angle) * length;
      const y2 = y + Math.sin(angle) * length;

      lines.push({
        x1: x,
        y1: y,
        x2,
        y2,
        opacity: 0.8
      });

      if (lines.length > maxLines) {
        lines.shift();
      }
    };

    const createParticle = (x: number, y: number) => {
      const angle = Math.random() * Math.PI * 2;
      const speed = Math.random() * 3 + 1;

      particles.push({
        x,
        y,
        size: Math.random() * 4 + 2,
        opacity: 0.6,
        speedX: Math.cos(angle) * speed,
        speedY: Math.sin(angle) * speed
      });

      if (particles.length > maxParticles) {
        particles.shift();
      }
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // 绘制线条
      lines.forEach((line, index) => {
        ctx.beginPath();
        ctx.strokeStyle = `rgba(0, 255, 148, ${line.opacity})`;
        ctx.lineWidth = 1.5;
        ctx.moveTo(line.x1, line.y1);
        ctx.lineTo(line.x2, line.y2);
        ctx.stroke();

        line.opacity -= 0.003;
        if (line.opacity <= 0) {
          lines.splice(index, 1);
        }
      });

      // 绘制粒子
      particles.forEach((particle, index) => {
        ctx.beginPath();
        ctx.fillStyle = `rgba(0, 255, 148, ${particle.opacity})`;
        ctx.arc(particle.x, particle.y, particle.size, 0, Math.PI * 2);
        ctx.fill();

        particle.x += particle.speedX;
        particle.y += particle.speedY;
        particle.opacity -= 0.008;

        if (particle.opacity <= 0) {
          particles.splice(index, 1);
        }
      });

      requestAnimationFrame(animate);
    };

    let lastTime = 0;
    const handleMouseMove = (e: MouseEvent) => {
      const currentTime = Date.now();
      if (currentTime - lastTime > 30) { // 更频繁地创建效果
        if (Math.random() > 0.5) {
          createLine(e.clientX, e.clientY);
        }
        if (Math.random() > 0.6) {
          createParticle(e.clientX, e.clientY);
        }
        lastTime = currentTime;
      }
    };

    // 自动创建一些初始效果
    const createInitialEffects = () => {
      for (let i = 0; i < 10; i++) {
        const x = Math.random() * canvas.width;
        const y = Math.random() * canvas.height;
        createLine(x, y);
        createParticle(x, y);
      }
    };

    createInitialEffects();
    canvas.addEventListener('mousemove', handleMouseMove);
    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      canvas.removeEventListener('mousemove', handleMouseMove);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      className="absolute top-0 left-0 w-full h-full pointer-events-none"
      style={{ zIndex: 0 }}
    />
  );
};

export default InteractiveLines;
