import React, { useRef, useEffect } from "react";
import { motion, useScroll, useTransform, useSpring, useInView } from "framer-motion";
import { Link } from "react-router-dom";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines-i";
import Footer from "./Footer";

const Infosafe: React.FC = () => {
  const { scrollY } = useScroll();
  const scrollX = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(scrollX, { stiffness: 100, damping: 30 });
  const heroRef = useRef(null);
  const heroInView = useInView(heroRef, { once: true, amount: 0.3 });
  const canvasRef = useRef<HTMLCanvasElement>(null);

  // Add refs for each section
  const gradientRef = useRef(null);
  const aboutRef = useRef(null);
  const featuresRef = useRef(null);
  const galleryRef = useRef(null);

  // Add inView hooks for each section
  const gradientInView = useInView(gradientRef, { once: true, amount: 0.3 });
  const aboutInView = useInView(aboutRef, { once: true, amount: 0.3 });
  const featuresInView = useInView(featuresRef, { once: true, amount: 0.3 });
  const galleryInView = useInView(galleryRef, { once: true, amount: 0.3 });

  const scrollToProjects = () => {
    if (window.location.pathname !== '/') {
      window.location.href = '/#projects';
    } else {
      const projectsSection = document.getElementById('projects');
      if (projectsSection) {
        projectsSection.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  useEffect(() => {
    console.log('Infosafe component mounted');
    console.log('Hero in view:', heroInView);
  }, [heroInView]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    const resizeCanvas = () => {
      canvas.width = canvas.offsetWidth;
      canvas.height = canvas.offsetHeight;
    };
    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    let isHovering = false;
    let mouseX = 0;
    let mouseY = 0;

    const handleMouseMove = (e: MouseEvent) => {
      const rect = canvas.getBoundingClientRect();
      mouseX = e.clientX - rect.left;
      mouseY = e.clientY - rect.top;
    };

    const handleMouseEnter = () => {
      isHovering = true;
    };

    const handleMouseLeave = () => {
      isHovering = false;
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    };

    canvas.addEventListener('mousemove', handleMouseMove);
    canvas.addEventListener('mouseenter', handleMouseEnter);
    canvas.addEventListener('mouseleave', handleMouseLeave);

    const drawGlowingLine = () => {
      if (!isHovering) return;

      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw main line
      ctx.beginPath();
      ctx.moveTo(0, mouseY);
      ctx.lineTo(canvas.width, mouseY);
      ctx.strokeStyle = '#FFB800';
      ctx.lineWidth = 2;
      ctx.stroke();

      // Draw glow effect
      ctx.beginPath();
      ctx.moveTo(0, mouseY);
      ctx.lineTo(canvas.width, mouseY);
      ctx.strokeStyle = '#FFB800';
      ctx.shadowColor = '#FFB800';
      ctx.shadowBlur = 15;
      ctx.lineWidth = 1;
      ctx.stroke();

      requestAnimationFrame(drawGlowingLine);
    };

    drawGlowingLine();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      canvas.removeEventListener('mousemove', handleMouseMove);
      canvas.removeEventListener('mouseenter', handleMouseEnter);
      canvas.removeEventListener('mouseleave', handleMouseLeave);
    };
  }, []);

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <motion.section 
        ref={heroRef}
        className="relative w-full min-h-[100vh] h-screen overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: heroInView ? 1 : 0 }}
        transition={{ duration: 1 }}
      >
        <InteractiveLines />
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-20 translate-y-50"
            animate={{
              y: [0, -20, 0],
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <motion.img 
              src="/card2.png" 
              alt="Infosafe" 
              className="w-48 sm:w-64 md:w-80 h-auto"
              whileHover={{
                scale: 1.1,
                transition: {
                  duration: 0.3,
                  ease: "easeOut"
                }
              }}
              onError={(e) => {
                console.error('Error loading image:', e);
                e.currentTarget.src = '/placeholder.png';
              }}
            />
          </motion.div>
        </div>
        <motion.h1
          className="relative z-10 text-[30vw] sm:text-[35vw] md:text-[30vw] font-serif font-bold text-right mt-[20vh] sm:mt-[25vh] md:mt-[30vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          INFOSAFE
        </motion.h1>
      </motion.section>

      {/* Gradient Section */}
      <motion.section 
        ref={gradientRef}
        className="relative w-full h-[400px] sm:h-[500px] md:h-[600px] bg-gradient-to-b from-[#FFB800] via-[#F4F1EC] via-[#F4F1EC] via-[#F4F1EC] via-[#F4F1EC] to-[#F4F1EC] py-6 sm:py-8 md:py-10 px-4 sm:px-6 md:px-8 flex items-center justify-center overflow-hidden"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: gradientInView ? 1 : 0, y: gradientInView ? 0 : 50 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
      >
        <canvas
          ref={canvasRef}
          className="absolute inset-0 w-full h-full"
        />
        <div className="absolute top-6 sm:top-8 md:top-10 w-full overflow-hidden z-20">
          <motion.div 
            className="whitespace-nowrap text-2xl sm:text-3xl md:text-4xl font-light italic font-inter text-black"
            initial={{ x: "100%" }}
            animate={{ x: "-100%" }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "linear"
            }}
          >
            Web Design and Development
          </motion.div>
        </div>

        <div className="relative flex items-center justify-center z-10">
          <h1 className="text-4xl sm:text-6xl md:text-8xl lg:text-[120px] font-semibold italic font-inter text-center px-4">Web Design and Development</h1>
        </div>
      </motion.section>

      {/* About and Login Portal Section */}
      <motion.section 
        ref={aboutRef}
        className="w-full min-h-screen bg-[#F4F1EC] py-8 sm:py-10 px-4 sm:px-6 md:px-8 lg:px-16"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: aboutInView ? 1 : 0, y: aboutInView ? 0 : 50 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
      >
        <div className="max-w-7xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-8 sm:gap-12">
          {/* About Section */}
          <motion.div 
            className="space-y-6 sm:space-y-8 flex flex-col justify-end"
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: aboutInView ? 1 : 0, x: aboutInView ? 0 : -50 }}
            transition={{ duration: 0.8, delay: 0.2, ease: "easeOut" }}
          >
            <h2 className="text-3xl sm:text-4xl md:text-5xl font-bold font-serif">About</h2>
            <p className="text-base sm:text-lg text-gray-700">
              A real-time platform for reporting and sharing incidents, enabling users to
              document emergencies or concerns and enhance community safety.
            </p>
            <div className="bg-yellow-400 p-4 sm:p-6 rounded-lg">
              <p className="font-medium text-sm sm:text-base">
                Goal: Enhance community safety awareness and responsiveness.
              </p>
            </div>
            <div className="mt-6 sm:mt-8">
              <img
                src="/info.png"
                alt="IN4SAFE"
                className="w-full sm:w-2/3 md:w-2/4 mx-auto rounded-lg shadow-lg"
              />
            </div>
          </motion.div>

          {/* Login Portal Image */}
          <motion.div 
            className="flex items-end justify-center mt-12 sm:mt-16 md:mt-20 overflow-visible relative"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: aboutInView ? 1 : 0, x: aboutInView ? 0 : 50 }}
            transition={{ duration: 0.8, delay: 0.4, ease: "easeOut" }}
          >
            <img
              src="/info-1.png"
              alt="IN4SAFE Login Portal"
              className="w-full max-w-[600px] rounded-lg shadow-lg"
            />
          </motion.div>
        </div>
      </motion.section>

      {/* Features Section */}
      <motion.section 
        ref={featuresRef}
        className="w-full min-h-screen bg-black py-12 sm:py-16 md:py-20 px-4 sm:px-6 md:px-8 lg:px-16"
        initial={{ opacity: 0 }}
        animate={{ opacity: featuresInView ? 1 : 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="max-w-7xl mx-auto">
          <motion.div 
            className="h-1 w-[200px] sm:w-[300px] md:w-[500px] bg-yellow-400 rounded mb-12 sm:mb-16 md:mb-20"
            initial={{ width: 0 }}
            animate={{ width: featuresInView ? "100%" : 0 }}
            transition={{ duration: 1, delay: 0.2 }}
          />

          {/* User Flow Section */}
          <motion.div 
            className="grid grid-cols-1 md:grid-cols-2 gap-8 sm:gap-12 md:gap-16 mb-16 sm:mb-24 md:mb-32"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: featuresInView ? 1 : 0, y: featuresInView ? 0 : 50 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <div className="text-white">
              <h2 className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-serif mb-4 sm:mb-6">User Flow</h2>
              <p className="text-base sm:text-lg md:text-xl text-gray-300">
                The basic information architecture and user flow at the minimum viable product stage.
              </p>
            </div>
            <div className="flex items-center justify-center">
              <img
                src="/info-flow.png"
                alt="User Flow Diagram"
                className="w-full sm:w-4/5 md:w-3/4"
              />
            </div>
          </motion.div>

          {/* Target Users Section */}
          <motion.div 
            className="bg-[#F4F1EC] py-12 sm:py-16 md:py-20 px-4 sm:px-6 md:px-8 rounded-lg"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: featuresInView ? 1 : 0, y: featuresInView ? 0 : 50 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <h2 className="text-3xl sm:text-4xl md:text-5xl font-serif text-black mb-8 sm:mb-12 md:mb-16">Target Users</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 sm:gap-8">
              <div className="flex flex-col items-center">
                <img
                  src="/info-user1.png"
                  alt="Community residents"
                  className="w-48 sm:w-56 md:w-68"
                />
              </div>
              <div className="flex flex-col items-center">
                <img
                  src="/info-user2.png"
                  alt="Community managers"
                  className="w-48 sm:w-56 md:w-68"
                />
              </div>
              <div className="flex flex-col items-center sm:col-span-2 md:col-span-1">
                <img
                  src="/info-user3.png"
                  alt="Public safety organizations"
                  className="w-48 sm:w-56 md:w-68"
                />
              </div>
            </div>
          </motion.div>
        </div>
      </motion.section>

      {/* Image Gallery Section */}
      <motion.section 
        ref={galleryRef}
        className="w-full bg-[#F4F1EC] py-12 sm:py-16 md:py-20 px-4 sm:px-6 md:px-8"
        initial={{ opacity: 0 }}
        animate={{ opacity: galleryInView ? 1 : 0 }}
        transition={{ duration: 0.8 }}
      >
        <div className="max-w-7xl mx-auto">
          <motion.h2 
            className="text-3xl sm:text-4xl md:text-5xl font-serif text-black mb-8 sm:mb-12 md:mb-16"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: galleryInView ? 1 : 0, y: galleryInView ? 0 : 20 }}
            transition={{ duration: 0.8 }}
          >
            Wireframe Gallery
          </motion.h2>
          <motion.div 
            className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-4 sm:gap-6 md:gap-8"
            initial={{ opacity: 0 }}
            animate={{ opacity: galleryInView ? 1 : 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <div className="flex flex-col items-center">
              <img
                src="/info-1.png"
                alt="Gallery image 1"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-2.png"
                alt="Gallery image 2"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-3.png"
                alt="Gallery image 3"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-4.png"
                alt="Gallery image 4"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-5.png"
                alt="Gallery image 5"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-6.png"
                alt="Gallery image 6"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-7.png"
                alt="Gallery image 7"
                className="w-full h-auto rounded-lg"
              />
            </div>
            <div className="flex flex-col items-center">
              <img
                src="/info-8.png"
                alt="Gallery image 8"
                className="w-full h-auto rounded-lg"
              />
            </div>
          </motion.div>
        </div>
      </motion.section>

      {/* View All Projects Button */}
      <motion.div 
        className="w-full bg-[#F4F1EC] py-8 sm:py-10 md:py-12 pb-16 sm:pb-20 md:pb-24 flex justify-center"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8 }}
      >
        <button 
          onClick={() => {
            window.location.href = '/all-projects';
            window.scrollTo(0, 0);
          }}
          className="bg-[#FFB800] text-black border border-black rounded-full px-6 sm:px-8 md:px-10 py-3 sm:py-4 font-serif text-sm sm:text-base font-medium transition-all duration-300 ease-in-out hover:-translate-y-1 hover:shadow-[0_8px_0_0_#000000] active:translate-y-0 active:shadow-[0_1px_0_0_#000000]"
        >
          VIEW ALL PROJECTS
        </button>
      </motion.div>

      <Footer />
    </div>
  );
};

export default Infosafe;
