import React from "react";
import Header from "./Header";

const WitnessCard = () => {
  return (
    <div className="relative w-[300px] h-[400px] bg-black text-white rounded-3xl p-6 flex flex-col items-center">
      <div className="text-2xl font-bold mb-4">WITNESS</div>
      <div className="w-24 h-24 relative mb-4">
        <div className="absolute w-full h-full bg-white rounded-full"></div>
        <div className="absolute w-16 h-8 bg-black top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"></div>
        <div className="absolute w-4 h-4 bg-red-500 rounded-full top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"></div>
      </div>
      <div className="mt-auto text-center">2025</div>
    </div>
  );
};

const Witness: React.FC = () => {
  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <main className="pt-24 px-8">
        <div className="max-w-6xl mx-auto">
          {/* Hero Section */}
          <div className="flex flex-col items-center mb-20">
            <div className="text-[120px] font-bold tracking-wider">WITNESS</div>
            <div className="relative">
              <WitnessCard />
            </div>
            <div className="text-xl mt-8 italic text-gray-700">
              User Interface & User Experience Design
            </div>
          </div>

          {/* Interactive Demo Section */}
          <section className="mb-20">
            <h2 className="text-5xl font-bold mb-8">Interactive demo</h2>
            <div className="w-full aspect-video bg-gray-700 rounded-lg">
              {/* Interactive demo content will go here */}
            </div>
          </section>
        </div>
      </main>
    </div>
  );
};

export default Witness;
