'use client';

import { useEffect, useRef } from 'react';
import { motion, useAnimation, useInView } from 'framer-motion';

interface InteractiveLinesProps {
  className?: string;
}

const InteractiveLines: React.FC<InteractiveLinesProps> = ({ className = '' }) => {
  const controls = useAnimation();
  const ref = useRef(null);
  const isInView = useInView(ref, { once: true });

  useEffect(() => {
    if (isInView) {
      controls.start('visible');
    }
  }, [controls, isInView]);

  const lineVariants = {
    hidden: { pathLength: 0, opacity: 0 },
    visible: {
      pathLength: 1,
      opacity: 1,
      transition: {
        duration: 2,
        ease: [0.22, 1, 0.36, 1],
      },
    },
  };

  return (
    <div ref={ref} className={`relative w-full h-full ${className}`}>
      <svg
        width="100%"
        height="100%"
        viewBox="0 0 1000 600"
        className="absolute inset-0"
        preserveAspectRatio="none"
      >
        {/* Main cross lines */}
        <motion.path
          d="M0,0 L1000,600"
          stroke="#FF0000"
          strokeWidth="3"
          fill="none"
          variants={lineVariants}
          initial="hidden"
          animate={controls}
        />
        <motion.path
          d="M1000,0 L0,600"
          stroke="#FF0000"
          strokeWidth="3"
          fill="none"
          variants={lineVariants}
          initial="hidden"
          animate={controls}
          transition={{ delay: 0.3 }}
        />
        
        {/* Decorative lines */}
        <motion.path
          d="M250,0 L750,600"
          stroke="#000000"
          strokeWidth="1.5"
          fill="none"
          variants={lineVariants}
          initial="hidden"
          animate={controls}
          transition={{ delay: 0.6 }}
        />
        <motion.path
          d="M750,0 L250,600"
          stroke="#000000"
          strokeWidth="1.5"
          fill="none"
          variants={lineVariants}
          initial="hidden"
          animate={controls}
          transition={{ delay: 0.9 }}
        />

        {/* Center circle */}
        <motion.circle
          cx="500"
          cy="300"
          r="100"
          stroke="#FF0000"
          strokeWidth="2"
          fill="none"
          variants={lineVariants}
          initial="hidden"
          animate={controls}
          transition={{ delay: 1.2 }}
        />
      </svg>
    </div>
  );
};

export default InteractiveLines;
