import React, { useEffect, useRef } from 'react';

const InteractiveLines: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    const pawPrints: { x: number; y: number; size: number; opacity: number }[] = [];
    const maxPawPrints = 50;

    const createPawPrint = (x: number, y: number) => {
      pawPrints.push({
        x,
        y,
        size: Math.random() * 20 + 10,
        opacity: 1
      });

      if (pawPrints.length > maxPawPrints) {
        pawPrints.shift();
      }
    };

    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      pawPrints.forEach((paw, index) => {
        ctx.beginPath();
        ctx.fillStyle = `rgba(0, 255, 148, ${paw.opacity})`;
        ctx.arc(paw.x, paw.y, paw.size, 0, Math.PI * 2);
        ctx.fill();

        paw.opacity -= 0.005;
        if (paw.opacity <= 0) {
          pawPrints.splice(index, 1);
        }
      });

      requestAnimationFrame(animate);
    };

    canvas.addEventListener('mousemove', (e) => {
      if (Math.random() > 0.9) {
        createPawPrint(e.clientX, e.clientY);
      }
    });

    animate();

    return () => {
      window.removeEventListener('resize', resizeCanvas);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      className="fixed top-0 left-0 w-full h-full pointer-events-none"
      style={{ zIndex: 1 }}
    />
  );
};

export default InteractiveLines;
