import React, { useRef, useEffect } from "react";
import { motion, useScroll, useTransform, useSpring, useInView } from "framer-motion";
import { Link } from "react-router-dom";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines-i";
import Footer from "./Footer";

const Infosafe: React.FC = () => {
  const { scrollY } = useScroll();
  const scrollX = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(scrollX, { stiffness: 100, damping: 30 });
  const heroRef = useRef(null);
  const heroInView = useInView(heroRef, { once: true, amount: 0.3 });
  const drawingRef = useRef<HTMLDivElement>(null);

  const scrollToProjects = () => {
    if (window.location.pathname !== '/') {
      window.location.href = '/#projects';
    } else {
      const projectsSection = document.getElementById('projects');
      if (projectsSection) {
        projectsSection.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  useEffect(() => {
    console.log('Infosafe component mounted');
    console.log('Hero in view:', heroInView);
  }, [heroInView]);

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <motion.section 
        ref={heroRef}
        className="relative w-full min-h-[100vh] h-screen overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: heroInView ? 1 : 0 }}
        transition={{ duration: 1 }}
      >
        <InteractiveLines />
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-20 translate-y-50"
            animate={{
              y: [0, -20, 0],
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <motion.img 
              src="/card2.png" 
              alt="Infosafe" 
              className="w-80 h-auto"
              whileHover={{
                scale: 1.1,
                transition: {
                  duration: 0.3,
                  ease: "easeOut"
                }
              }}
              onError={(e) => {
                console.error('Error loading image:', e);
                e.currentTarget.src = '/placeholder.png';
              }}
            />
          </motion.div>
        </div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          INFOSAFE
        </motion.h1>
      </motion.section>

      {/* Gradient Section */}
      <motion.section 
        className="relative w-full h-[800px] bg-gradient-to-b from-[#FFB800] via-[#F4F1EC] via-[#F4F1EC] via-[#F4F1EC] via-[#F4F1EC] to-[#F4F1EC] py-10 px-8 flex items-center justify-center overflow-hidden"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        {/* Mouse drawing effect */}
        <div 
          ref={drawingRef}
          className="absolute inset-0 cursor-crosshair"
          onMouseMove={(e) => {
            if (!drawingRef.current) return;

            const square = document.createElement('div');
            square.className = 'absolute w-4 h-4 bg-[#FFB800]';
            square.style.left = `${e.clientX}px`;
            square.style.top = `${e.clientY}px`;
            drawingRef.current.appendChild(square);

            // Remove square after 2 seconds
            setTimeout(() => {
              if (square.parentNode) {
                square.parentNode.removeChild(square);
              }
            }, 2000);
          }}
        />

        <div className="absolute top-10 w-full overflow-hidden">
          <motion.div 
            className="whitespace-nowrap text-4xl font-light italic font-inter text-black"
            initial={{ x: "100%" }}
            animate={{ x: "-100%" }}
