import React from "react";
import "../components/css/Projects.css";

type ProjectItem = {
  title: string;
  subtitle: string;
};

const projectList: ProjectItem[] = [
  {
    title: "'PAWMEET' Application",
    subtitle: "UI & UX",
  },
  {
    title: "InfoSafe Application",
    subtitle: "Web Design",
  },
  {
    title: "Urban Legend Application",
    subtitle: "UI & UX",
  }
];

const Projects: React.FC = () => {
  return (
    <section
      id="projects"
      className="w-full px-4 md:px-6 py-16 md:py-20 bg-[#f3f2ea] text-black font-serif"
    >
      <div className="max-w-6xl mx-auto">
        {/* Wrap divider and title in a div, ensure left alignment */}
        <div className="-mt-10 text-left"> 
          <div className="h-[1px] w-full max-w-[250px] bg-gray-300 mb-8" />
          <h2 className="projects-title">Projects</h2>
        </div>

        {/* Project list container - Removed border and background image */}
        <div className="relative overflow-hidden">
          {/* Image tag removed */}

          {/* 项目列表 - Responsive top padding */}
          <div className="relative z-10 pt-[30px] md:pt-[53px]">
            {projectList.map((item, idx) => (
              <div
                key={idx}
                className={`group px-4 md:px-6 py-8 md:py-14 border-t border-black transition-all duration-300 ${
                  idx === 0 ? "border-t-0" : ""
                } hover:bg-gradient-to-r hover:from-cyan-100 hover:to-white`}
              >
                <h3 className="text-lg md:text-xl font-semibold font-serif">
                  {item.title}
                </h3>
                <p className="text-sm md:text-base text-gray-600 mt-1 font-sans">{item.subtitle}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
