import React from "react";

const About: React.FC = () => {
  return (
    <section
      id="about"
      className="w-full bg-[#f9f7f2] px-6 py-20 font-serif text-black"
    >
      <div className="max-w-6xl mx-auto">
        {/* 分隔线 */}
        <div className="h-[1px] w-full bg-gray-300 mb-8" />

        {/* 标题 */}
        <h2 className="text-xl font-bold mb-6">About</h2>

        {/* 内容区域 */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-8 items-start">
          {/* 左边介绍 */}
          <div className="space-y-4 text-base leading-relaxed">
            <p>
              I’m <strong>Celeste</strong>, a product designer and manager,
              with experience working with new technologies.
            </p>
            <p>
              I deeply focus on UI/UX and product design, creating excellent
              design through our whole lives.
            </p>
          </div>

          {/* 右边用图片代替 */}
          <div className="w-full">
            <img
              src="/public/frame1.png"
              alt="Resume Box"
              className="w-full h-auto border border-black"
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default About;
