import React, { useState } from 'react';
import { motion } from 'framer-motion';

const InteractiveLines: React.FC = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Generate random positions for lines and points
  const generateElements = (count: number) => {
    return Array.from({ length: count }, () => ({
      x: Math.random() * 100,
      y: Math.random() * 100,
      angle: Math.random() * 360,
      length: Math.random() * 150 + 100, // Longer lines
      delay: Math.random() * 0.5,
    }));
  };

  const lines = generateElements(12); // More lines
  const points = generateElements(20); // More points

  const handleMouseMove = (e: React.MouseEvent) => {
    const { clientX, clientY } = e;
    setMousePosition({
      x: (clientX / window.innerWidth) * 100,
      y: (clientY / window.innerHeight) * 100
    });
  };

  return (
    <div 
      className="absolute inset-0 overflow-hidden"
      onMouseMove={handleMouseMove}
    >
      <motion.div
        className="absolute top-0 left-0 w-full h-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {/* Dynamic Lines */}
        {lines.map((line, index) => (
          <motion.div
            key={`line-${index}`}
            className="absolute origin-left"
            style={{
              left: `${line.x}%`,
              top: `${line.y}%`,
              width: `${line.length}px`,
              height: '3px', // Thicker lines
              background: 'linear-gradient(to right, #4CAF50, #8BC34A)',
              transform: `rotate(${line.angle}deg)`,
            }}
            initial={{ scaleX: 0 }}
            animate={{ 
              scaleX: 1,
              rotate: line.angle + Math.sin(Date.now() * 0.001 + index) * 20, // More rotation
            }}
            transition={{
              duration: 1.5,
              delay: line.delay,
              repeat: Infinity,
              repeatType: "reverse",
              ease: "easeInOut"
            }}
            whileHover={{
              scale: 1.5,
              background: 'linear-gradient(to right, #2E7D32, #4CAF50)',
              transition: { duration: 0.3 }
            }}
          />
        ))}

        {/* Floating Points */}
        {points.map((point, index) => {
          const distance = Math.sqrt(
            Math.pow(point.x - mousePosition.x, 2) + 
            Math.pow(point.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.8, 1.5 - distance / 30); // Larger scale range

          return (
            <motion.div
              key={`point-${index}`}
              className="absolute rounded-full"
              style={{
                left: `${point.x}%`,
                top: `${point.y}%`,
                width: '30px', // Larger points
                height: '30px',
                background: 'radial-gradient(circle, #4CAF50, #8BC34A)',
                boxShadow: '0 0 20px rgba(76, 175, 80, 0.5)', // Glow effect
              }}
              initial={{ scale: 0 }}
              animate={{ 
                scale: scale,
                x: Math.sin(Date.now() * 0.001 + index) * 20, // Larger movement
                y: Math.cos(Date.now() * 0.001 + index) * 20,
              }}
              transition={{
                duration: 0.8,
                delay: point.delay,
                repeat: Infinity,
                repeatType: "reverse",
                ease: "easeInOut"
              }}
              whileHover={{
                scale: 2,
                background: 'radial-gradient(circle, #2E7D32, #4CAF50)',
                boxShadow: '0 0 30px rgba(46, 125, 50, 0.8)',
                transition: { duration: 0.3 }
              }}
            />
          );
        })}

        {/* Background Grid */}
        <div className="absolute inset-0 grid grid-cols-12 grid-rows-12">
          {Array.from({ length: 144 }).map((_, index) => (
            <motion.div
              key={`grid-${index}`}
              className="border border-[#4CAF50]/20"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{
                duration: 1.5,
                delay: index * 0.02,
              }}
            />
          ))}
        </div>

        {/* Mouse Trail Effect */}
        <motion.div
          className="absolute rounded-full pointer-events-none"
          style={{
            width: '100px',
            height: '100px',
            background: 'radial-gradient(circle, rgba(76, 175, 80, 0.2), transparent 70%)',
          }}
          animate={{
            x: mousePosition.x * window.innerWidth / 100 - 50,
            y: mousePosition.y * window.innerHeight / 100 - 50,
          }}
          transition={{ type: "spring", stiffness: 50, damping: 20 }}
        />
      </motion.div>
    </div>
  );
};

export default InteractiveLines;
