import React, { useRef } from "react";
import { motion, useScroll, useTransform, useSpring } from "framer-motion";
import InteractiveLines from "./InteractiveLines";

const Hero: React.FC = () => {
  const { scrollY } = useScroll();
  const x = useTransform(scrollY, [0, 400], [200, -200]);
  const xSpring = useSpring(x, { stiffness: 100, damping: 30 });

  return (
    <section className="relative w-full min-h-[100vh] h-screen overflow-hidden bg-white flex flex-col justify-center">
      <InteractiveLines />

      <motion.h1
        className="relative z-10 text-[30vw] md:text-[25vw] lg:text-[20vw] font-serif font-bold text-right mt-[20vh] md:mt-[15vh] pr-[5vw] whitespace-nowrap"
        style={{ x: xSpring }}
      >
        CELESTE
      </motion.h1>
    </section>
  );
};

export default Hero;
