import React, { useRef } from "react";
import { motion, useScroll, useTransform, useSpring, useInView } from "framer-motion";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines-i";
import Footer from "./Footer";

const WitnessCard = () => {
  return (
    <div className="relative w-[300px] h-[400px] bg-black text-white rounded-3xl p-6 flex flex-col items-center">
      <div className="text-2xl font-bold mb-4">WITNESS</div>
      <div className="w-24 h-24 relative mb-4">
        <div className="absolute w-full h-full bg-white rounded-full"></div>
        <div className="absolute w-16 h-8 bg-black top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"></div>
        <div className="absolute w-4 h-4 bg-red-500 rounded-full top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"></div>
      </div>
      <div className="mt-auto text-center">2025</div>
    </div>
  );
};

const Witness: React.FC = () => {
  const { scrollY } = useScroll();
  const scrollX = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(scrollX, { stiffness: 100, damping: 30 });
  
  // Add ref for hero section
  const heroRef = useRef(null);
  const heroInView = useInView(heroRef, { once: true, amount: 0.3 });

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <motion.section 
        ref={heroRef}
        className="relative w-full min-h-[100vh] h-screen overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: heroInView ? 1 : 0 }}
        transition={{ duration: 1 }}
      >
        <InteractiveLines />
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-20 translate-y-50"
            animate={{
              y: [0, -20, 0],
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <WitnessCard />
          </motion.div>
        </div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          WITNESS
        </motion.h1>
      </motion.section>

      <motion.section 
        className="relative w-full min-h-screen bg-[#F4F1EC] py-20 px-8"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: heroInView ? 1 : 0, y: heroInView ? 0 : 50 }}
        transition={{ duration: 1 }}
      >
        <div className="max-w-7xl mx-auto">
          <div className="w-80 h-[2px] bg-black mb-8"></div>
          <h2 className="text-5xl font-bold mb-8">Interactive demo</h2>
          <div className="w-full aspect-video bg-gray-700 rounded-lg">
            {/* Interactive demo content will go here */}
          </div>
        </div>
      </motion.section>
    </div>
  );
};

export default Witness;
