import React, { useEffect, useRef } from 'react';

interface Point {
  x: number;
  y: number;
  baseY: number;
}

const InteractiveLines: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const mousePos = useRef({ x: 0, y: 0 });
  const points = useRef<Point[][]>([]);
  const animationFrameId = useRef<number>();

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    const setCanvasSize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };
    setCanvasSize();
    window.addEventListener('resize', setCanvasSize);

    // Initialize grid points
    const initGrid = () => {
      const gridSize = 30;
      const cols = Math.floor(canvas.width / gridSize);
      const rows = Math.floor(canvas.height / gridSize);

      points.current = [];
      for (let i = 0; i < cols; i++) {
        const col: Point[] = [];
        for (let j = 0; j < rows; j++) {
          col.push({
            x: i * gridSize,
            y: j * gridSize,
            baseY: j * gridSize,
          });
        }
        points.current.push(col);
      }
    };
    initGrid();

    // Mouse move handler
    const handleMouseMove = (e: MouseEvent) => {
      const rect = canvas.getBoundingClientRect();
      mousePos.current = {
        x: e.clientX - rect.left,
        y: e.clientY - rect.top,
      };
    };
    canvas.addEventListener('mousemove', handleMouseMove);

    // Animation
    const animate = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.strokeStyle = '#4A90E2';
      ctx.lineWidth = 1;

      // Update points
      for (let i = 0; i < points.current.length; i++) {
        for (let j = 0; j < points.current[i].length; j++) {
          const point = points.current[i][j];
          const dx = mousePos.current.x - point.x;
          const dy = mousePos.current.y - point.y;
          const distance = Math.sqrt(dx * dx + dy * dy);
          const force = Math.max(0, 1 - distance / 200);
          
          point.y = point.baseY + Math.sin(distance * 0.03) * force * 50;
        }
      }

      // Draw lines
      for (let i = 0; i < points.current.length; i++) {
        ctx.beginPath();
        ctx.moveTo(points.current[i][0].x, points.current[i][0].y);
        
        for (let j = 1; j < points.current[i].length; j++) {
          ctx.lineTo(points.current[i][j].x, points.current[i][j].y);
        }
        ctx.stroke();
      }

      animationFrameId.current = requestAnimationFrame(animate);
    };
    animate();

    // Cleanup
    return () => {
      window.removeEventListener('resize', setCanvasSize);
      canvas.removeEventListener('mousemove', handleMouseMove);
      if (animationFrameId.current) {
        cancelAnimationFrame(animationFrameId.current);
      }
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        width: '100%',
        height: '100%',
        background: '#F4F1EC',
      }}
    />
  );
};

export default InteractiveLines;
