import React from "react";

type ProjectItem = {
  title: string;
  subtitle: string;
};

const projectList: ProjectItem[] = [
  {
    title: "‘PAWMEET’ Application",
    subtitle: "UI & UX",
  },
  {
    title: "InfoSafe Application",
    subtitle: "Web Design",
  },
  {
    title: "Urban Legend Application",
    subtitle: "UI & UX",
  },
  {
    title: "Echo",
    subtitle: "UI design and Web game",
  },
];

const Projects: React.FC = () => {
  return (
    <section
      id="projects"
      className="w-full px-6 py-20 bg-[#f3f2ea] text-black font-serif"
    >
      <div className="max-w-5xl mx-auto">
        <h2 className="text-3xl font-bold mb-8">Projects</h2>

        {/* 大框背景图 */}
        <div className="relative border border-black overflow-hidden">
          <img
            src="/assets/ProjectBox.png"
            alt="Project Box Background"
            className="absolute top-0 left-0 w-full h-full object-cover pointer-events-none z-0"
          />

          {/* 项目列表 */}
          <div className="relative z-10">
            {projectList.map((item, idx) => (
              <div
                key={idx}
                className={`group px-6 py-6 border-t border-black transition-all duration-300 ${
                  idx === 0 ? "border-t-0" : ""
                } hover:bg-gradient-to-r hover:from-cyan-100 hover:to-white`}
              >
                <h3 className="text-lg font-semibold group-hover:italic">
                  {item.title}
                </h3>
                <p className="text-sm text-gray-600 mt-1">{item.subtitle}</p>
              </div>
            ))}

            {/* 右上角叉叉 */}
            <div className="absolute top-0 right-0 border-l border-b border-black px-3 py-2 text-sm">
              ×
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
