import React, { useState } from 'react';
import { motion } from 'framer-motion';

const InteractiveLines: React.FC = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Generate random positions for lines and points
  const generateElements = (count: number) => {
    return Array.from({ length: count }, () => ({
      x: Math.random() * 100,
      y: Math.random() * 100,
      angle: Math.random() * 360,
      length: Math.random() * 100 + 50,
      delay: Math.random() * 0.5,
    }));
  };

  const lines = generateElements(8);
  const points = generateElements(12);

  const handleMouseMove = (e: React.MouseEvent) => {
    const { clientX, clientY } = e;
    setMousePosition({
      x: (clientX / window.innerWidth) * 100,
      y: (clientY / window.innerHeight) * 100
    });
  };

  return (
    <div 
      className="absolute inset-0 overflow-hidden"
      onMouseMove={handleMouseMove}
    >
      <motion.div
        className="absolute top-0 left-0 w-full h-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {/* Dynamic Lines */}
        {lines.map((line, index) => (
          <motion.div
            key={`line-${index}`}
            className="absolute origin-left"
            style={{
              left: `${line.x}%`,
              top: `${line.y}%`,
              width: `${line.length}px`,
              height: '2px',
              background: 'linear-gradient(to right, #FFB6C1, #87CEEB)',
              transform: `rotate(${line.angle}deg)`,
            }}
            initial={{ scaleX: 0 }}
            animate={{ 
              scaleX: 1,
              rotate: line.angle + Math.sin(Date.now() * 0.001 + index) * 10,
            }}
            transition={{
              duration: 1,
              delay: line.delay,
              repeat: Infinity,
              repeatType: "reverse",
              ease: "easeInOut"
            }}
            whileHover={{
              scale: 1.2,
              background: 'linear-gradient(to right, #FFD700, #FFB6C1)',
              transition: { duration: 0.2 }
            }}
          />
        ))}

        {/* Floating Points */}
        {points.map((point, index) => {
          const distance = Math.sqrt(
            Math.pow(point.x - mousePosition.x, 2) + 
            Math.pow(point.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.5, 1 - distance / 50);

          return (
            <motion.div
              key={`point-${index}`}
              className="absolute rounded-full"
              style={{
                left: `${point.x}%`,
                top: `${point.y}%`,
                width: '20px',
                height: '20px',
                background: 'radial-gradient(circle, #FFD700, #FFB6C1)',
              }}
              initial={{ scale: 0 }}
              animate={{ 
                scale: scale,
                x: Math.sin(Date.now() * 0.001 + index) * 10,
                y: Math.cos(Date.now() * 0.001 + index) * 10,
              }}
              transition={{
                duration: 0.5,
                delay: point.delay,
                repeat: Infinity,
                repeatType: "reverse",
                ease: "easeInOut"
              }}
              whileHover={{
                scale: 1.5,
                background: 'radial-gradient(circle, #FFB6C1, #FFD700)',
                transition: { duration: 0.2 }
              }}
            />
          );
        })}

        {/* Background Grid */}
        <div className="absolute inset-0 grid grid-cols-12 grid-rows-12">
          {Array.from({ length: 144 }).map((_, index) => (
            <motion.div
              key={`grid-${index}`}
              className="border border-[#FFB6C1]/10"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{
                duration: 1,
                delay: index * 0.01,
              }}
            />
          ))}
        </div>
      </motion.div>
    </div>
  );
};

export default InteractiveLines;
