import React, { useRef, useState } from "react";
import { motion, useScroll, useTransform, useSpring, AnimatePresence } from "framer-motion";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines";

const Pawmeet: React.FC = () => {
  const { scrollY } = useScroll();
  const x = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(x, { stiffness: 100, damping: 30 });
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  const images = ["/p-1.png", "/p-2.png", "/p-3.png"];

  React.useEffect(() => {
    const timer = setInterval(() => {
      setCurrentImageIndex((prev) => (prev + 1) % images.length);
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(timer);
  }, []);

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <section className="relative w-full min-h-[100vh] h-screen overflow-hidden">
        <InteractiveLines />
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-20 translate-y-50"
            animate={{
              y: [0, -20, 0],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <img src="/card1.png" alt="Card" className="w-80 h-auto" />
          </motion.div>
        </div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          PAWMEET
        </motion.h1>
      </section>
      
      <section className="relative w-full h-[900px] bg-gradient-to-b from-[#B2FFC8] to-white py-10 px-8 flex items-center justify-center overflow-hidden">
        {/* Background paw prints */}
        <div className="absolute w-[280px] h-[280px] left-20 bottom-20">
          <img 
            src="/p-bg.png" 
            alt="Background Paw Print" 
            className="w-full h-full object-contain"
          />
        </div>
        <div className="absolute w-[280px] h-[280px] right-20 top-20">
          <img 
            src="/p-bg.png" 
            alt="Background Paw Print" 
            className="w-full h-full object-contain"
          />
        </div>

        <div className="absolute top-10 w-full overflow-hidden">
          <motion.div 
            className="whitespace-nowrap text-4xl font-light italic font-inter text-black"
            initial={{ x: "100%" }}
            animate={{ x: "-100%" }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "linear"
            }}
          >
            Front-end and Back-end Development
          </motion.div>
        </div>
        
        <div className="relative flex items-center justify-center">
          <motion.div
            initial={{ y: -100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{
              duration: 1,
              ease: "easeOut"
            }}
            className="absolute -top-40 w-[500px] h-[500px] z-20"
          >
            <AnimatePresence mode="wait">
              <motion.img 
                key={currentImageIndex}
                src={images[currentImageIndex]}
                alt={`Project Image ${currentImageIndex + 1}`}
                className="w-full h-full object-contain"
                loading="eager"
                decoding="sync"
                fetchPriority="high"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                transition={{
                  duration: 0.5,
                  ease: "easeInOut"
                }}
              />
            </AnimatePresence>
          </motion.div>
          <h1 className="text-[120px] font-semibold italic font-inter">UI/UX DESIGN</h1>
        </div>
      </section>
    </div>
  );
};

export default Pawmeet;
