import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./BlobCursor.css"; // 添加新的CSS文件

const BlobCursor: React.FC = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isHovered, setIsHovered] = useState(false);

  useEffect(() => {
    // 添加全局样式来隐藏默认光标
    document.documentElement.style.cursor = 'none';
    document.body.style.cursor = 'none';
    
    // 添加到所有可交互元素
    const elements = document.querySelectorAll('a, button, input, [role="button"]');
    elements.forEach(el => {
      (el as HTMLElement).style.cursor = 'none';
    });
    
    const move = (e: MouseEvent) => {
      setPosition({ x: e.clientX, y: e.clientY });
    };

    const handleMouseOver = (e: MouseEvent) => {
      if ((e.target as HTMLElement).tagName === 'A' || (e.target as HTMLElement).tagName === 'BUTTON') {
        setIsHovered(true);
      }
    };

    const handleMouseOut = () => {
      setIsHovered(false);
    };

    window.addEventListener("mousemove", move);
    document.addEventListener("mouseover", handleMouseOver);
    document.addEventListener("mouseout", handleMouseOut);

    return () => {
      document.documentElement.style.cursor = 'auto';
      document.body.style.cursor = 'auto';
      elements.forEach(el => {
        (el as HTMLElement).style.cursor = 'auto';
      });
      window.removeEventListener("mousemove", move);
      document.removeEventListener("mouseover", handleMouseOver);
      document.removeEventListener("mouseout", handleMouseOut);
    };
  }, []);

  const blobVariants = {
    default: {
      borderRadius: [
        "60% 40% 30% 70% / 60% 30% 70% 40%",
        "30% 60% 70% 40% / 50% 60% 30% 60%",
        "60% 40% 30% 70% / 60% 30% 70% 40%",
        "40% 60% 70% 30% / 50% 60% 30% 60%"
      ],
      rotate: [0, 120, 240, 360],
      transition: {
        duration: 12,
        repeat: Infinity,
        ease: "easeInOut",
        times: [0, 0.333, 0.666, 1]
      }
    },
    hover: {
      borderRadius: [
        "70% 30% 50% 50% / 30% 50% 50% 70%",
        "50% 50% 70% 30% / 50% 30% 70% 50%",
        "30% 70% 70% 30% / 30% 30% 70% 70%",
        "70% 30% 50% 50% / 30% 50% 50% 70%"
      ],
      scale: 1.2,
      rotate: [0, 120, 240, 360],
      transition: {
        duration: 8,
        repeat: Infinity,
        ease: "easeInOut",
        times: [0, 0.333, 0.666, 1]
      }
    }
  };

  return (
    <>
      {/* 更大的黑点光标 */}
      <div
        style={{
          width: '10px',          // 增加大小
          height: '10px',         // 增加大小
          backgroundColor: 'black',
          borderRadius: '50%',
          position: 'fixed',
          pointerEvents: 'none',
          zIndex: 60,
          top: position.y - 5,    // 调整偏移以保持居中
          left: position.x - 5,   // 调整偏移以保持居中
          transition: 'transform 0.1s ease'
        }}
      />

      {/* 原有的 blob 效果保持不变 */}
      <motion.div
        className="pointer-events-none fixed top-0 left-0 z-50"
        animate={{
          x: position.x - 25,
          y: position.y - 25,
        }}
        transition={{
          type: "spring",
          stiffness: 100,
          damping: 25,
          mass: 1,
          restDelta: 0.001
        }}
      >
        <motion.div 
          style={{
            width: '50px',
            height: '50px',
            border: '1px solid black',
            backgroundColor: 'transparent',
          }}
          initial="default"
          animate={isHovered ? "hover" : "default"}
          variants={blobVariants}
        />
        {/* 添加延迟跟随的第二个 blob */}
        <motion.div 
          style={{
            width: '50px',
            height: '50px',
            border: '1px solid black',
            backgroundColor: 'transparent',
            position: 'absolute',
            top: 0,
            left: 0,
            opacity: 0.5,
          }}
          initial="default"
          animate={isHovered ? "hover" : "default"}
          variants={blobVariants}
          transition={{
            type: "spring",
            stiffness: 50,   // 更低的刚度
            damping: 30,     // 更高的阻尼
            mass: 1.5,       // 更大的质量
            delay: 0.1       // 添加延迟
          }}
        />
      </motion.div>
    </>
  );
};

export default BlobCursor;
