import React, { useRef } from "react";
import { motion, useScroll, useTransform, useSpring } from "framer-motion";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines";

const Pawmeet: React.FC = () => {
  const { scrollY } = useScroll();
  const x = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(x, { stiffness: 100, damping: 30 });

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <section className="relative w-full min-h-[100vh] h-screen overflow-hidden flex flex-col justify-center">
        <InteractiveLines />
        <motion.div
          className="absolute top-1/4 left-1/4 z-20"
          animate={{
            y: [0, -20, 0],
          }}
          transition={{
            duration: 4,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        >
          <img src="/card1.png" alt="Card" className="w-64 h-auto" />
        </motion.div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          PAWMEET
        </motion.h1>
      </section>
      
      <section className="relative w-full min-h-screen bg-white py-20 px-8">
        <div className="max-w-4xl mx-auto">
          <h2 className="text-4xl font-bold mb-8">项目介绍</h2>
          <div className="prose prose-lg">
            <p className="mb-4">
              PAWMEET 是一个宠物社交平台，旨在连接宠物主人和爱宠人士。
            </p>
            <p className="mb-4">
              通过这个平台，用户可以分享宠物的日常生活，组织线下聚会，以及交流养宠经验。
            </p>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Pawmeet;
