import React, { useState } from 'react';
import { motion } from 'framer-motion';

const InteractiveLines: React.FC = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Generate positions for circles
  const generateCircles = () => {
    return [
      { x: 30, y: 30, size: 80, delay: 0 },
      { x: 70, y: 30, size: 60, delay: 0.2 },
      { x: 30, y: 70, size: 60, delay: 0.4 },
      { x: 70, y: 70, size: 80, delay: 0.6 },
    ];
  };

  const circles = generateCircles();

  const handleMouseMove = (e: React.MouseEvent) => {
    const { clientX, clientY } = e;
    setMousePosition({
      x: (clientX / window.innerWidth) * 100,
      y: (clientY / window.innerHeight) * 100
    });
  };

  return (
    <div 
      className="absolute inset-0 overflow-hidden"
      onMouseMove={handleMouseMove}
    >
      <motion.div
        className="absolute top-0 left-0 w-full h-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {/* Bouncing Circles */}
        {circles.map((circle, index) => {
          const distance = Math.sqrt(
            Math.pow(circle.x - mousePosition.x, 2) + 
            Math.pow(circle.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.8, 1.3 - distance / 40);

          return (
            <motion.div
              key={`circle-${index}`}
              className="absolute rounded-full"
              style={{
                left: `${circle.x}%`,
                top: `${circle.y}%`,
                width: `${circle.size}px`,
                height: `${circle.size}px`,
                background: 'radial-gradient(circle, #4CAF50, #8BC34A)',
                transform: 'translate(-50%, -50%)',
                boxShadow: '0 0 30px rgba(76, 175, 80, 0.5)',
              }}
              initial={{ scale: 0 }}
              animate={{ 
                scale: scale,
                y: [0, -20, 0],
                rotate: Math.sin(Date.now() * 0.001 + index) * 360,
              }}
              transition={{
                duration: 2,
                delay: circle.delay,
                repeat: Infinity,
                ease: "easeInOut",
                y: {
                  duration: 2,
                  repeat: Infinity,
                  ease: "easeInOut"
                }
              }}
              whileHover={{
                scale: 1.2,
                background: 'radial-gradient(circle, #2E7D32, #4CAF50)',
                boxShadow: '0 0 40px rgba(46, 125, 50, 0.8)',
                transition: { duration: 0.3 }
              }}
            />
          );
        })}

        {/* Dynamic Lines */}
        {circles.map((circle, index) => {
          const nextCircle = circles[(index + 1) % circles.length];
          return (
            <motion.div
              key={`line-${index}`}
              className="absolute origin-left"
              style={{
                left: `${circle.x}%`,
                top: `${circle.y}%`,
                width: `${Math.sqrt(
                  Math.pow(nextCircle.x - circle.x, 2) + 
                  Math.pow(nextCircle.y - circle.y, 2)
                ) * window.innerWidth / 100}px`,
                height: '2px',
                background: 'linear-gradient(to right, #4CAF50, #8BC34A)',
                transform: `rotate(${Math.atan2(
                  nextCircle.y - circle.y,
                  nextCircle.x - circle.x
                ) * 180 / Math.PI}deg)`,
                transformOrigin: '0 50%',
              }}
              initial={{ scaleX: 0 }}
              animate={{ 
                scaleX: 1,
                opacity: [0.3, 0.7, 0.3],
              }}
              transition={{
                duration: 2,
                delay: index * 0.2,
                repeat: Infinity,
                repeatType: "reverse",
                ease: "easeInOut"
              }}
            />
          );
        })}

        {/* Mouse Trail */}
        <motion.div
          className="absolute rounded-full pointer-events-none"
          style={{
            width: '150px',
            height: '150px',
            background: 'radial-gradient(circle, rgba(76, 175, 80, 0.2), transparent 70%)',
            boxShadow: '0 0 30px rgba(76, 175, 80, 0.2)',
          }}
          animate={{
            x: mousePosition.x * window.innerWidth / 100 - 75,
            y: mousePosition.y * window.innerHeight / 100 - 75,
            scale: [1, 1.2, 1],
          }}
          transition={{ 
            type: "spring", 
            stiffness: 50, 
            damping: 20,
            scale: {
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }
          }}
        />
      </motion.div>
    </div>
  );
};

export default InteractiveLines;
