import React from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, Float, Sphere } from "@react-three/drei";

const Hero: React.FC = () => {
  return (
    <section className="relative w-full h-[80vh] overflow-hidden">
      {/* 3D 背景 Canvas */}
      <Canvas className="absolute top-0 left-0 w-full h-full z-0">
        {/* 光照 */}
        <ambientLight intensity={0.4} />
        <directionalLight position={[2, 2, 2]} intensity={1} />

        {/* 浮动变形球体 */}
        <Float speed={2} rotationIntensity={1} floatIntensity={1.5}>
          <Sphere args={[1, 64, 64]} scale={2.8}>
            <meshStandardMaterial
              color="#a0e2ff"
              roughness={0.25}
              metalness={0.2}
            />
          </Sphere>
        </Float>

        <OrbitControls enableZoom={false} autoRotate />
      </Canvas>

      {/* 前景文字 */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full text-center">
        <p className="text-xs italic text-gray-400 mb-4">
          *add 3D elements in background
        </p>
        <h1 className="text-[13vw] font-serif font-bold tracking-tight leading-none">
          CELESTE
        </h1>
      </div>
    </section>
  );
};

export default Hero;
