import React, { useRef, useEffect, useState } from "react";
import { motion, useScroll, useTransform, useSpring, useInView } from "framer-motion";
import { Link } from "react-router-dom";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines-i";
import Footer from "./Footer";

const Infosafe: React.FC = () => {
  const { scrollY } = useScroll();
  const scrollX = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(scrollX, { stiffness: 100, damping: 30 });
  const heroRef = useRef(null);
  const heroInView = useInView(heroRef, { once: true, amount: 0.3 });
  const [isDrawing, setIsDrawing] = useState(false);
  const [lastPos, setLastPos] = useState({ x: 0, y: 0 });

  const scrollToProjects = () => {
    if (window.location.pathname !== '/') {
      window.location.href = '/#projects';
    } else {
      const projectsSection = document.getElementById('projects');
      if (projectsSection) {
        projectsSection.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  useEffect(() => {
    console.log('Infosafe component mounted');
    console.log('Hero in view:', heroInView);
  }, [heroInView]);

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <motion.section 
        ref={heroRef}
        className="relative w-full min-h-[100vh] h-screen overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: heroInView ? 1 : 0 }}
        transition={{ duration: 1 }}
      >
        <InteractiveLines />
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-20 translate-y-50"
            animate={{
              y: [0, -20, 0],
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <motion.img 
              src="/card2.png" 
              alt="Infosafe" 
              className="w-80 h-auto"
              whileHover={{
                scale: 1.1,
                transition: {
                  duration: 0.3,
                  ease: "easeOut"
                }
              }}
              onError={(e) => {
                console.error('Error loading image:', e);
                e.currentTarget.src = '/placeholder.png';
              }}
            />
          </motion.div>
        </div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          INFOSAFE
        </motion.h1>
      </motion.section>

      {/* Gradient Section */}
      <motion.section 
        className="relative w-full h-[600px] bg-gradient-to-b from-[#FFB800] via-[#F4F1EC] via-[#F4F1EC] via-[#F4F1EC] via-[#F4F1EC] to-[#F4F1EC] py-10 px-8 flex items-center justify-center overflow-hidden"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 1 }}
      >
        {/* Mouse drawing effect */}
        <div 
          className="absolute inset-0 cursor-crosshair z-30"
          onMouseDown={(e) => {
            setIsDrawing(true);
            setLastPos({ x: e.clientX, y: e.clientY });
          }}
          onMouseUp={() => setIsDrawing(false)}
          onMouseMove={(e) => {
            if (!isDrawing) return;

            const line = document.createElement('div');
            line.className = 'absolute h-1 bg-[#FFB800] z-30';
            line.style.left = `${lastPos.x}px`;
            line.style.top = `${lastPos.y}px`;

            const length = Math.sqrt(
              Math.pow(e.clientX - lastPos.x, 2) + 
              Math.pow(e.clientY - lastPos.y, 2)
            );
            line.style.width = `${length}px`;

            const angle = Math.atan2(
              e.clientY - lastPos.y,
              e.clientX - lastPos.x
            ) * 180 / Math.PI;
            line.style.transform = `rotate(${angle}deg)`;
            line.style.boxShadow = '0 0 10px #FFB800, 0 0 20px #FFB800';
            line.style.opacity = '0.8';

            e.currentTarget.appendChild(line);
            setLastPos({ x: e.clientX, y: e.clientY });

            // Remove line after 2 seconds
            setTimeout(() => {
              line.remove();
            }, 2000);
          }}
        />

