import React, { useState } from 'react';
import { motion } from 'framer-motion';

const InteractiveLines: React.FC = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  // Generate positions for lines and dots
  const generateElements = () => {
    return {
      lines: [
        { x: 20, y: 50, angle: 0, length: 100 }, // Longer horizontal lines
        { x: 40, y: 50, angle: 0, length: 100 },
        { x: 60, y: 50, angle: 0, length: 100 },
        { x: 80, y: 50, angle: 0, length: 100 },
        { x: 50, y: 20, angle: 90, length: 100 }, // Longer vertical lines
        { x: 50, y: 40, angle: 90, length: 100 },
        { x: 50, y: 60, angle: 90, length: 100 },
        { x: 50, y: 80, angle: 90, length: 100 },
      ],
      dots: [
        { x: 20, y: 20, size: 12 }, // Larger dots
        { x: 80, y: 20, size: 12 },
        { x: 20, y: 80, size: 12 },
        { x: 80, y: 80, size: 12 },
      ]
    };
  };

  const { lines, dots } = generateElements();

  const handleMouseMove = (e: React.MouseEvent) => {
    const { clientX, clientY } = e;
    setMousePosition({
      x: (clientX / window.innerWidth) * 100,
      y: (clientY / window.innerHeight) * 100
    });
  };

  return (
    <div 
      className="absolute inset-0 overflow-hidden"
      onMouseMove={handleMouseMove}
    >
      <motion.div
        className="absolute top-0 left-0 w-full h-full"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
      >
        {/* Lines */}
        {lines.map((line, index) => {
          const distance = Math.sqrt(
            Math.pow(line.x - mousePosition.x, 2) + 
            Math.pow(line.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.3, 1.2 - distance / 30); // More responsive scaling

          return (
            <motion.div
              key={`line-${index}`}
              className="absolute origin-left"
              style={{
                left: `${line.x}%`,
                top: `${line.y}%`,
                width: `${line.length}px`,
                height: '3px', // Thicker lines
                background: '#4CAF50',
                transform: `rotate(${line.angle}deg)`,
                transformOrigin: '0 50%',
                boxShadow: '0 0 10px rgba(76, 175, 80, 0.5)', // Glow effect
              }}
              initial={{ scaleX: 0 }}
              animate={{ 
                scaleX: scale,
                opacity: scale,
                y: Math.sin(Date.now() * 0.001 + index) * 5, // Subtle movement
              }}
              transition={{
                duration: 0.2,
                ease: "easeOut"
              }}
            />
          );
        })}

        {/* Dots */}
        {dots.map((dot, index) => {
          const distance = Math.sqrt(
            Math.pow(dot.x - mousePosition.x, 2) + 
            Math.pow(dot.y - mousePosition.y, 2)
          );
          const scale = Math.max(0.3, 1.2 - distance / 30); // More responsive scaling

          return (
            <motion.div
              key={`dot-${index}`}
              className="absolute rounded-full"
              style={{
                left: `${dot.x}%`,
                top: `${dot.y}%`,
                width: `${dot.size}px`,
                height: `${dot.size}px`,
                background: '#4CAF50',
                transform: 'translate(-50%, -50%)',
                boxShadow: '0 0 15px rgba(76, 175, 80, 0.5)', // Glow effect
              }}
              initial={{ scale: 0 }}
              animate={{ 
                scale: scale,
                opacity: scale,
                y: Math.sin(Date.now() * 0.001 + index) * 5, // Subtle movement
              }}
              transition={{
                duration: 0.2,
                ease: "easeOut"
              }}
            />
          );
        })}

        {/* Mouse Trail */}
        <motion.div
          className="absolute rounded-full pointer-events-none"
          style={{
            width: '150px',
            height: '150px',
            background: 'radial-gradient(circle, rgba(76, 175, 80, 0.2), transparent 70%)',
            boxShadow: '0 0 30px rgba(76, 175, 80, 0.2)', // Stronger glow
          }}
          animate={{
            x: mousePosition.x * window.innerWidth / 100 - 75,
            y: mousePosition.y * window.innerHeight / 100 - 75,
            scale: [1, 1.2, 1], // Pulsing effect
          }}
          transition={{ 
            type: "spring", 
            stiffness: 50, 
            damping: 20,
            scale: {
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }
          }}
        />
      </motion.div>
    </div>
  );
};

export default InteractiveLines;
