import React, { useRef } from "react";
import { motion, useScroll, useTransform, useSpring, useInView } from "framer-motion";
import Header from "./Header";
import InteractiveLines from "./InteractiveLines-w";
import Footer from "./Footer";

const Witness: React.FC = () => {
  const { scrollY } = useScroll();
  const scrollX = useTransform(scrollY, [0, 400], [0, -200]);
  const xSpring = useSpring(scrollX, { stiffness: 100, damping: 30 });
  
  // Add ref for hero section
  const heroRef = useRef(null);
  const heroInView = useInView(heroRef, { once: true, amount: 0.3 });

  const uiSectionRef = useRef(null);
  const uiSectionInView = useInView(uiSectionRef, { once: true, amount: 0.3 });

  const aboutSectionRef = useRef(null);
  const aboutSectionInView = useInView(aboutSectionRef, { once: true, amount: 0.3 });

  return (
    <div className="relative min-h-screen bg-[#F4F1EC]">
      <Header />
      <motion.section 
        ref={heroRef}
        className="relative w-full min-h-[100vh] h-screen overflow-hidden"
        initial={{ opacity: 0 }}
        animate={{ opacity: heroInView ? 1 : 0 }}
        transition={{ duration: 1 }}
      >
        <div className="absolute inset-0 z-0">
          <InteractiveLines />
        </div>
        <motion.h1
          className="relative z-10 text-[40vw] md:text-[35vw] lg:text-[30vw] font-serif font-bold text-right mt-[45vh] md:mt-[25vh] pr-[5vw] whitespace-nowrap"
          style={{ x: xSpring }}
        >
          WITNESS
        </motion.h1>
        <div className="absolute inset-0 flex items-center justify-center">
          <motion.div
            className="relative z-50"
            animate={{
              y: [0, -20, 0],
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          >
            <motion.img 
              src="/card3.png" 
              alt="Card" 
              className="w-80 h-auto"
              whileHover={{
                scale: 1.1,
                transition: {
                  duration: 0.3,
                  ease: "easeOut"
                }
              }}
            />
          </motion.div>
        </div>
        <div className="absolute bottom-0 left-0 right-0 h-[30vh] bg-gradient-to-t from-[#F4F1EC] to-transparent z-40"></div>
      </motion.section>

      <motion.section 
        ref={uiSectionRef}
        className="relative w-full h-[800px] bg-gradient-to-b from-[#F0584E] via-[#F5F2ED] via-[#F5F2ED] via-[#F5F2ED] via-[#F5F2ED] to-[#F5F2ED] py-10 px-8 flex items-center justify-center overflow-hidden"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: uiSectionInView ? 1 : 0, y: uiSectionInView ? 0 : 50 }}
        transition={{ duration: 1 }}
      >
        {/* Background eye icons */}
        <div className="absolute w-[280px] h-[280px] left-20 bottom-20 rotate-[-15deg]">
          <img 
            src="/w-bg.png" 
            alt="Background Eye" 
            className="w-full h-full object-contain"
          />
        </div>
        <div className="absolute w-[280px] h-[280px] right-20 top-20 rotate-[15deg]">
          <img 
            src="/w-bg.png" 
            alt="Background Eye" 
            className="w-full h-full object-contain"
          />
        </div>

        <div className="absolute top-10 w-full overflow-hidden">
          <motion.div 
            className="whitespace-nowrap text-4xl font-light italic font-inter text-black"
            initial={{ x: "100%" }}
            animate={{ x: "-100%" }}
            transition={{
              duration: 20,
              repeat: Infinity,
              ease: "linear"
            }}
          >
            User Interface & User Experience Design
          </motion.div>
        </div>
        
        <div className="relative flex items-center justify-center z-10">
          <motion.div
            initial={{ y: -100, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{
              duration: 1,
              ease: "easeOut"
            }}
            className="absolute -top-40 w-[600px] h-[600px] z-20"
          >
            <motion.img 
              src="/w-1.png"
              alt="Witness Design"
              className="w-full h-full object-contain"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{
                duration: 0.5,
                ease: "easeInOut"
              }}
            />
          </motion.div>
          <h1 className="text-[120px] font-semibold italic font-inter">UI/UX DESIGN</h1>
        </div>
      </motion.section>

      <motion.section 
        className="relative w-full h-[500px]"
        initial={{ opacity: 0 }}
        animate={{ opacity: uiSectionInView ? 1 : 0 }}
        transition={{ duration: 1 }}
      >
        <img 
          src="/w-bg2.png" 
          alt="Background" 
          className="w-full h-full object-cover object-center"
        />
      </motion.section>

      <motion.section
        ref={aboutSectionRef}
        className="relative w-full min-h-screen bg-[#F5F5F0] py-20 px-8"
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: aboutSectionInView ? 1 : 0, y: aboutSectionInView ? 0 : 50 }}
        transition={{ duration: 1 }}
      >
        <div className="max-w-7xl mx-auto flex items-center gap-8">
          <div className="flex-1 pl-32 -mt-20">
            <div className="w-80 h-[2px] bg-[#F0584E] mb-8"></div>
            <div className="flex items-center gap-4 mb-8">
              <img 
                src="/eye.png" 
                alt="Eye Icon" 
                className="w-12 h-12"
              />
              <h2 className="text-6xl font-bold">About</h2>
            </div>
            <p className="text-xl leading-relaxed italic text-gray-700">
              "WITNESS" is a UI/UX design project that explores the intersection of interactive storytelling and user experience. 
              The project focuses on creating immersive digital experiences through innovative interface design, emphasizing user-centered 
              interaction patterns and modern design principles.
            </p>
          </div>
          
          <div className="flex-1 flex justify-center">
            <div className="relative w-[250px]">
              <img 
                src="/w-mobile.png" 
                alt="Phone Mockup" 
                className="w-full h-auto"
              />
            </div>
          </div>
        </div>
      </motion.section>

      <motion.section
        className="relative w-full py-32 px-8 bg-[#F4F1EC]"
        initial={{ opacity: 0, y: 50 }}
        whileInView={{ opacity: 1, y: 0 }}
        viewport={{ once: true, amount: 0.3 }}
        transition={{ duration: 0.8 }}
      >
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-16">
            <div>
              <div className="w-60 h-[2px] bg-[#F0584E] mb-8"></div>
              <h2 className="text-4xl font-bold mb-6">Project Overview</h2>
              <p className="text-lg text-gray-700 mb-8">
                Witness is a UI/UX design project that explores the intersection of interactive storytelling and user experience. The project focuses on creating immersive digital experiences through innovative interface design.
              </p>
              <ul className="space-y-4">
                <li className="flex items-start">
                  <div className="w-2 h-2 rounded-full bg-black mt-2 mr-4"></div>
                  <p className="text-lg text-gray-700">Responsive and adaptive design system</p>
                </li>
                <li className="flex items-start">
                  <div className="w-2 h-2 rounded-full bg-black mt-2 mr-4"></div>
                  <p className="text-lg text-gray-700">Interactive animations and transitions</p>
                </li>
                <li className="flex items-start">
                  <div className="w-2 h-2 rounded-full bg-black mt-2 mr-4"></div>
                  <p className="text-lg text-gray-700">User-centered interaction patterns</p>
                </li>
              </ul>
            </div>
            <div className="relative">
              <motion.div
                className="absolute -top-10 -right-10 w-40 h-40 bg-[#E8E4DD] rounded-full"
                animate={{
                  scale: [1, 1.2, 1],
                  rotate: [0, 90, 0],
                }}
                transition={{
                  duration: 8,
                  repeat: Infinity,
                  ease: "easeInOut",
                }}
              />
              <div className="relative z-10 bg-white p-8 rounded-lg shadow-xl">
                <h3 className="text-2xl font-bold mb-6">Key Features</h3>
                <div className="space-y-6">
                  <div>
                    <div className="flex items-center mb-2">
                      <div className="w-8 h-8 bg-black rounded-full flex items-center justify-center text-white mr-3">1</div>
                      <h4 className="text-xl font-semibold">Modern Design Language</h4>
                    </div>
                    <p className="text-gray-600 ml-11">Clean, minimalist approach with focus on typography and spacing</p>
                  </div>
                  <div>
                    <div className="flex items-center mb-2">
                      <div className="w-8 h-8 bg-black rounded-full flex items-center justify-center text-white mr-3">2</div>
                      <h4 className="text-xl font-semibold">Motion Design</h4>
                    </div>
                    <p className="text-gray-600 ml-11">Thoughtful animations that enhance user experience</p>
                  </div>
                  <div>
                    <div className="flex items-center mb-2">
                      <div className="w-8 h-8 bg-black rounded-full flex items-center justify-center text-white mr-3">3</div>
                      <h4 className="text-xl font-semibold">Accessibility</h4>
                    </div>
                    <p className="text-gray-600 ml-11">WCAG compliant design ensuring universal usability</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </motion.section>
    </div>
  );
};

export default Witness;
