import React, { useRef } from "react";
import { motion, useScroll, useTransform, useSpring } from "framer-motion";
import InteractiveLines from "./InteractiveLines";

const Hero: React.FC = () => {
  const { scrollY } = useScroll();
  const x = useTransform(scrollY, [0, 400], [100, -200]);
  const xSpring = useSpring(x, { stiffness: 100, damping: 30 });

  return (
    <section className="relative w-full min-h-[100vh] h-screen overflow-hidden bg-white flex flex-col justify-center">
      <div className="absolute inset-0 z-0">
        <InteractiveLines />
      </div>

      <motion.h1
        className="relative z-10 text-[40vw] sm:text-[35vw] md:text-[30vw] lg:text-[25vw] font-serif font-bold text-right mt-[50vh] sm:mt-[45vh] md:mt-[60vh] lg:mt-[35vh] pr-[5vw] sm:pr-[8vw] md:pr-[10vw] lg:pr-[12vw] whitespace-nowrap"
        style={{ x: xSpring }}
      >
        CELESTE
      </motion.h1>

      {/* Scroll indicator */}
      <motion.div 
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-20 flex flex-col items-center cursor-pointer"
        animate={{
          y: [0, 10, 0],
        }}
        transition={{
          duration: 1.5,
          repeat: Infinity,
          ease: "easeInOut"
        }}
        onClick={() => window.scrollTo({ top: window.innerHeight, behavior: 'smooth' })}
      >
        <span className="text-sm text-gray-600 mb-2">Scroll Down</span>
        <svg 
          width="24" 
          height="24" 
          viewBox="0 0 24 24" 
          fill="none" 
          stroke="currentColor" 
          strokeWidth="2" 
          strokeLinecap="round" 
          strokeLinejoin="round"
          className="text-gray-600"
        >
          <path d="M12 5v14M19 12l-7 7-7-7"/>
        </svg>
      </motion.div>
    </section>
  );
};

export default Hero;
